function crcCalc(pbuf)
{
    let n = pbuf.length;
    let crc = 0xffff;
    for(let i = 0; i < n; i++)
    {
        crc = crc ^ pbuf[i];
        for(let j = 0; j < 8; j++)
        {
            let a = crc;
            let carry_flag = a & 0x0001;
            crc = crc >> 1;
            if ( carry_flag == 1 ) crc = crc ^ 0xa001;
        }
    }
    return crc;
}

